import pandas as pd
import numpy as np

def main():
    print('Creating dataframe...')
    data = pd.DataFrame({
        'numbers': [1, 2, 3, 4, 5],
        'squared': [1, 4, 9, 16, 25]
    })
    print(f'Dataframe has {len(data)} rows')

    mean_val = float(data['numbers'].mean())
    sum_val = int(np.sum(data['squared']))

    return {
        'mean': mean_val,
        'sum_of_squares': sum_val,
        'array_stats': {
            'min': int(data['numbers'].min()),
            'max': int(data['numbers'].max())
        }
    }

